mapper.select:
select id, email, nickname, password
from user
where id=#{id}

select count(*)
from user
where

    id = #{id}


    email = #{email}


    nickname = #{nickname}

select id, email, nickname, password
from user
where id=#{id}

mapper.update:
update user
set password=#{password}
, email=#{email}, nickname=#{nickname}
where id=#{id}

update user
set token = #{token}
where id = #{userId}

mapper.select:
select token
from user
where id = #{userId}

mapper.update:
update user
set token = #{token, jdbcType=VARCHAR}
where id = #{userId}
